mapper.select:
select
id, username, password, realname, phoneid, idcard, photoaddress, altertime, uuid, salt
from myrhythm.user


        and id = #{id}


        and username = #{username}


        and password = #{password}


        and realname = #{realname}


        and phoneid = #{phoneid}


        and idcard = #{idcard}


        and photoaddress = #{photoaddress}


        and altertime = #{altertime}


        and uuid = #{uuid}


        and salt = #{salt}
